+        background-size: cover;
+        background-repeat: no-repeat;
+        background-position: center center;
+        overflow: hidden;
+        position: relative;
+    }
+    .grid-item {
+        overflow: hidden;
+        padding: 4px !important;
+    }
+    a{
+        position: absolute;
+        left: 0;
+        top: 0;
+        width: 100%;
+        height: 100%;
